import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import './Header.css';

export const Header = () => {
  const [username,setUsername] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Add this line

  useEffect(()=>{
    fetch('http://localhost:9000/profile',{
      credentials:'include'
    })
    .then(res => res.json())
    .then(data => {
      setUsername(data.username);
      setIsLoggedIn(true); // Set isLoggedIn to true if fetch is successful
    })
    .catch(err => {
      console.error(err);
      setIsLoggedIn(false); // Set isLoggedIn to false if fetch fails
    });
  },[])

  const logout = () => {
    fetch('http://localhost:9000/logout', {
      method: 'POST',
      credentials: 'include',
    })
    .then(() => {
      setUsername(null);
      setIsLoggedIn(false);
      return <Navigate to="/loginsignup" />; // Add this line
    })
    .catch(console.error);
  };

  return (
    <header className="header">
      <div className="logo-container">
        <img src="path_to_your_logo.png" alt="logo" className="logo" />
        <Link className="title" to='/'>MyBlog</Link>
      </div>
      <nav className="navigation">
        <ul className="nav-links">
          {isLoggedIn ? (
            <>
              <li><Link to="/create">Create Post</Link></li>
              <li><button onClick={logout}>Logout</button></li>
            </>
          ) : (
            <>
              <li><Link to="/loginsignup">Login</Link></li>
              <li><Link to="/loginsignup">Signup</Link></li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};
