import React, { useEffect, useState } from "react";
import { Header } from "../../Components/Header";
import { useParams } from "react-router-dom";
import { UserContextProvider } from "../../Components/UserContext";
import './PostPage.css';
import { format, formatISO9075 } from "date-fns";

export const PostPage = () => {
    const [postInfo, setPostInfo] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        fetch(`http://localhost:9000/post/${id}`).then(response =>{
            response.json().then(postInfo =>{
                setPostInfo(postInfo);
            })
        })
      }, [id]);

    return (
        <UserContextProvider>
            <>
                <Header />
                <div className="postpage">
                {postInfo && <h1>{postInfo.title}</h1>}
                    <time>{postInfo && postInfo.createdAt ? formatISO9075(new Date(postInfo.createdAt)) : ''}</time>
                    {postInfo && postInfo.author && <div className="author">by {postInfo.author.username}</div>}
                    {postInfo && postInfo.author && <div className="author">by {postInfo.author.username}</div>}


                    <div className="img">
                {postInfo && postInfo.cover && <img  src={`http://localhost:9000/${postInfo.cover}`} alt="" />}
                </div>
                {postInfo && postInfo.content && <div className="content" dangerouslySetInnerHTML={{ __html: postInfo.content }} />}
                </div>
            </>
        </UserContextProvider>

    );
};
